import { __amplify } from '../types';
import { customOpFactory } from './operations/custom';
const operationTypeMap = {
    queries: 'query',
    mutations: 'mutation',
};
export function generateCustomOperationsProperty(client, config, operationsType) {
    // some bundlers end up with `Amplify.configure` being called *after* generate client.
    // if that occurs, we need to *not error* while we wait. handling for late configuration
    // occurs in `generateClient()`. we do not need to subscribe to Hub events here.
    if (!config) {
        return {};
    }
    const modelIntrospection = config.modelIntrospection;
    // model intro schema might be absent if there's not actually a configured GraphQL API
    if (!modelIntrospection) {
        return {};
    }
    // custom operations will be absent from model intro schema if no custom ops
    // are present on the source schema.
    const operations = modelIntrospection[operationsType];
    if (!operations) {
        return {};
    }
    const ops = {};
    const useContext = client[__amplify] === null;
    for (const operation of Object.values(operations)) {
        ops[operation.name] = customOpFactory(client, modelIntrospection, operationTypeMap[operationsType], operation, useContext);
    }
    return ops;
}
export function generateCustomMutationsProperty(client, config) {
    return generateCustomOperationsProperty(client, config, 'mutations');
}
export function generateCustomQueriesProperty(client, config) {
    return generateCustomOperationsProperty(client, config, 'queries');
}
